
public class TA04Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Declaración de las variables
		int Num1 =2;
		int Num2 = 5;
		int Resultado;
		
		//Suma de Num1 + Num2
		Resultado = Num1 + Num2;
		System.out.println("La suma de " + Num1 + " + " + Num2 + " es " + (Resultado));
		
		//Resta de Num1 - Num2
		Resultado = Num1 - Num2;
		System.out.println("La resta de " + Num1 + " - " + Num2 + " es " + (Resultado));
		
		//Multiplicacón de Num1 * Num2
		Resultado = Num1 * Num2;
		System.out.println("La multiplicacón de " + Num1 + " * " + Num2 + " es " + (Resultado));

		//División de Num1 * Num2
		Resultado = Num1 * Num2;
		System.out.println("La división de " + Num1 + " / " + Num2 + " es " + (Resultado));
		
		//Módulo de Num1 % Num2
		Resultado = Num1 * Num2;
		System.out.println("El módulo de " + Num1 + " % " + Num2 + " es " + (Resultado));
		
		System.out.println("");
	}

}
